import React, { useState } from 'react';
import { connect } from 'react-redux';
import { addGoal } from './store';
import './NewGoalForm.css'; // Assuming you have a CSS file for styling

const NewGoalForm = (props) => {
  const [showForm, setShowForm] = useState(false); // State to toggle form display

  const {
    category,
    description,
    deadline,
    setCategory,
    setDescription,
    setDeadline,
    resetForm,
  } = props;

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleDeadlineChange = (e) => {
    setDeadline(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform form submission logic here, e.g., dispatching an action or API request

    // Reset form fields

    const goal = {
      category,
      description,
      deadline,
    };
    props.addGoal(goal);
  

    resetForm();
  };

  const toggleForm = () => {
    setShowForm((prevShowForm) => !prevShowForm);
  };

  return (
    <div className="card">
      <h2>New Goal Form</h2>
      <button className="toggle-button" onClick={toggleForm}>
        {showForm ? 'Hide Form' : 'Show Form'}
      </button>
      {showForm && (
        <form id="" onSubmit={handleSubmit} className="form">
          <div>
            <label>
              Category:
              <input
                type="text"
                value={category}
                onChange={handleCategoryChange}
              />
            </label>
          </div>
          <div>
            <label>
              Description:
              <textarea
                value={description}
                onChange={handleDescriptionChange}
              ></textarea>
            </label>
          </div>
          <div>
            <label>
              Deadline:
              <input
                type="date"
                value={deadline}
                onChange={handleDeadlineChange}
              />
            </label>
          </div>
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};

const mapStateToProps = (state) => ({
  category: state.category,
  description: state.description,
  deadline: state.deadline,
});

const mapDispatchToProps = (dispatch) => ({
  setCategory: (category) => dispatch({ type: 'SET_CATEGORY', payload: category }),
  setDescription: (description) => dispatch({ type: 'SET_DESCRIPTION', payload: description }),
  setDeadline: (deadline) => dispatch({ type: 'SET_DEADLINE', payload: deadline }),
  resetForm: () => dispatch({ type: 'RESET_FORM' })
});

export default connect(mapStateToProps, mapDispatchToProps)(NewGoalForm);
