//main state that renders all components
import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import GoalForm from './Components/GoalForm';
import GoalList from './Components/GoalList';
import ProgressBar from './Components/ProgressBar';
import CompletedGoalsPage from './CompletedGoalsPage';

const App = () => {
  const [goals, setGoals] = useState([]);
  const [completedGoals, setCompletedGoals] = useState([]);

  const addGoal = (goal) => {
    setGoals([...goals, goal]);
  };

  const completeGoal = (goal) => {
    setGoals(goals.filter((g) => g !== goal));
    setCompletedGoals([...completedGoals, goal]);
  };

  const calculateProgress = () => {
    const totalGoals = goals.length + completedGoals.length;
    const progress = (completedGoals.length / totalGoals) * 100;
    return progress.toFixed(2);
  };

  return (
    <Router>
      <div>
        <h1>Goal Tracker</h1>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/completed">Completed Goals</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route path="/" element={<Home goals={goals} addGoal={addGoal} completeGoal={completeGoal} calculateProgress={calculateProgress} />} />
          <Route path="/completed" element={<CompletedGoalsPage completedGoals={completedGoals} />} />
        </Routes>
      </div>
    </Router>
  );
};

const Home = ({ goals, addGoal, completeGoal, calculateProgress }) => {
  return (
    <>
      <GoalForm addGoal={addGoal} />
      <GoalList goals={goals} completeGoal={completeGoal} />
      <ProgressBar progress={calculateProgress()} />
    </>
  );
};

export default App;
