import React from 'react';
import { NavLink, Route, Switch, useRouteMatch } from 'react-router-dom';
import GoalForm from './GoalForm';
import GoalList from './GoalList';
import CompletedGoalsPage from './CompletedGoalsPage';
import ProgressBar from './ProgressBar';
import './Home.css';

const Home = ({ addGoal, goals, completeGoal, calculateProgress }) => {
  const { url } = useRouteMatch();

  return (
    <div className="container">
      <div className="tabs">
        <NavLink exact to={`${url}`} activeClassName="active">
          New
        </NavLink>
        <NavLink to={`${url}/goals`} activeClassName="active">
          Goals
        </NavLink>
        <NavLink to={`${url}/completed`} activeClassName="active">
          Completed Goals
        </NavLink>
      </div>

      <Switch>
        <Route exact path={`${url}`}>
          <GoalForm addGoal={addGoal} />
        </Route>
        <Route path={`${url}/goals`}>
          <GoalList goals={goals} completeGoal={completeGoal} />
        </Route>
        <Route path={`${url}/completed`}>
          <CompletedGoalsPage completedGoals={completedGoals} />
        </Route>
      </Switch>

      <ProgressBar progress={calculateProgress()} />
    </div>
  );
};

export default Home;
