import { createStore } from 'redux';

// Define initial state
const initialState = {
  category: '',
  description: '',
  deadline: '',
  goals: [],
};

// Reducer function
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_CATEGORY':
      return { ...state, category: action.payload };
    case 'SET_DESCRIPTION':
      return { ...state, description: action.payload };
    case 'SET_DEADLINE':
      return { ...state, deadline: action.payload };
    case 'ADD_GOAL':
      return { ...state, goals: [...state.goals, action.payload] }; // Add the new goal to the goals array
    case 'RESET_FORM':
      return initialState;
    default:
      return state;
  }
};

// Create the store
const store = createStore(rootReducer);

export default store;
