import React, { useState } from 'react';
import './NewGoalForm.css'; // Assuming you have a CSS file for styling

const NewGoalForm = (props) => {

  return (
    <div className='mb-5'>
      <form>
        <div className='mb-3'>Organize by Category
          <label>Category</label>
          <input></input>
        </div>
        <div className='mb-3'>
          <label>Description</label>
          <textarea></textarea>
        </div>
        <div className='mb-3'>
          <label>Deadline</label>
          <input type='date'></input>
        </div>
        <button type='button' className='btn btn-primary mt-3'>Submit Goal</button>
      </form>
    </div>
  );
};


export default NewGoalForm;
