import React, { useState } from 'react';
import GoalForm from './GoalForm';
import GoalList from './GoalList';
import ProgressBar from './ProgressBar';
import './Home.css';

const Home = ({ goals, completeGoal, calculateProgress }) => {
  const [goalCategories, setGoalCategories] = useState({});

  const addGoal = (goal) => {
    const { category } = goal;

    // Check if the category already exists
    if (goalCategories.hasOwnProperty(category)) {
      // Add the goal to the existing category
      setGoalCategories((prevCategories) => ({
        ...prevCategories,
        [category]: [...prevCategories[category], goal],
      }));
    } else {
      // Create a new category with the goal
      setGoalCategories((prevCategories) => ({
        ...prevCategories,
        [category]: [goal],
      }));
    }
  };

  return (
    <div className="container">
      <div className="card">
        <GoalForm addGoal={addGoal} />
      </div>
      <GoalList goalCategories={goalCategories} completeGoal={completeGoal} />
      <ProgressBar progress={calculateProgress()} />
    </div>
  );
};

export default Home;
